// @generated by Cursor AI (Claude) — verified by Kevin

import { useState, useRef, useEffect } from 'react';
import { Play, Pause, Volume2, VolumeX } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { useTranslation } from 'react-i18next';

/**
 * Composant AudioPlayer - Lecteur audio pour les signalements vocaux
 * 
 * Fonctionnalités :
 * - Lecture/Pause
 * - Contrôle du volume
 * - Affichage de la durée et progression
 * - Design responsive et accessible
 * 
 * @param {Object} props
 * @param {string} props.audioUrl - URL de l'audio à jouer
 * @param {string} [props.className] - Classes CSS additionnelles
 * 
 * @example
 * <AudioPlayer audioUrl={report.audio_url} />
 */
function AudioPlayer({ audioUrl, className = '' }) {
  const { t } = useTranslation('common');
  const audioRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [volume, setVolume] = useState(1);
  const [isMuted, setIsMuted] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  /**
   * Formater le temps en MM:SS
   */
  const formatTime = (seconds) => {
    if (!isFinite(seconds) || isNaN(seconds)) return '0:00';
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  /**
   * Charger les métadonnées de l'audio
   */
  useEffect(() => {
    if (!audioRef.current || !audioUrl) return;

    const audio = audioRef.current;

    const handleLoadedMetadata = () => {
      setDuration(audio.duration);
      setIsLoading(false);
      setError(null);
    };

    const handleTimeUpdate = () => {
      setCurrentTime(audio.currentTime);
    };

    const handleEnded = () => {
      setIsPlaying(false);
      setCurrentTime(0);
    };

    const handleError = (e) => {
      console.error('❌ Erreur lecture audio:', e);
      setIsLoading(false);
      setError(t('audio.error', { defaultValue: 'Erreur lors de la lecture de l\'audio' }));
      setIsPlaying(false);
    };

    const handleCanPlay = () => {
      setIsLoading(false);
    };

    audio.addEventListener('loadedmetadata', handleLoadedMetadata);
    audio.addEventListener('timeupdate', handleTimeUpdate);
    audio.addEventListener('ended', handleEnded);
    audio.addEventListener('error', handleError);
    audio.addEventListener('canplay', handleCanPlay);

    // Charger l'audio
    audio.load();

    return () => {
      audio.removeEventListener('loadedmetadata', handleLoadedMetadata);
      audio.removeEventListener('timeupdate', handleTimeUpdate);
      audio.removeEventListener('ended', handleEnded);
      audio.removeEventListener('error', handleError);
      audio.removeEventListener('canplay', handleCanPlay);
    };
  }, [audioUrl, t]);

  /**
   * Toggle play/pause
   */
  const togglePlayPause = () => {
    if (!audioRef.current) return;

    if (isPlaying) {
      audioRef.current.pause();
      setIsPlaying(false);
    } else {
      audioRef.current.play().catch((err) => {
        console.error('❌ Erreur lecture:', err);
        setError(t('audio.playError', { defaultValue: 'Impossible de lire l\'audio' }));
      });
      setIsPlaying(true);
    }
  };

  /**
   * Changer la position de lecture
   */
  const handleSeek = (e) => {
    if (!audioRef.current) return;
    const newTime = parseFloat(e.target.value);
    audioRef.current.currentTime = newTime;
    setCurrentTime(newTime);
  };

  /**
   * Changer le volume
   */
  const handleVolumeChange = (e) => {
    if (!audioRef.current) return;
    const newVolume = parseFloat(e.target.value);
    setVolume(newVolume);
    audioRef.current.volume = newVolume;
    setIsMuted(newVolume === 0);
  };

  /**
   * Toggle mute
   */
  const toggleMute = () => {
    if (!audioRef.current) return;
    if (isMuted) {
      audioRef.current.volume = volume || 0.5;
      setIsMuted(false);
    } else {
      audioRef.current.volume = 0;
      setIsMuted(true);
    }
  };

  if (!audioUrl) {
    return (
      <div className={`p-4 bg-neutral-50 rounded-lg border border-neutral-200 ${className}`}>
        <p className="text-sm text-neutral-500 text-center">
          {t('audio.noAudio', { defaultValue: 'Aucun enregistrement audio disponible' })}
        </p>
      </div>
    );
  }

  if (error) {
    return (
      <div className={`p-4 bg-red-50 rounded-lg border border-red-200 ${className}`}>
        <p className="text-sm text-red-700 text-center">{error}</p>
      </div>
    );
  }

  const progress = duration > 0 ? (currentTime / duration) * 100 : 0;

  return (
    <div className={`bg-white rounded-lg border border-neutral-200 p-4 ${className}`}>
      {/* Audio element (hidden) */}
      <audio ref={audioRef} src={audioUrl} preload="metadata" />

      {/* Controls */}
      <div className="space-y-3">
        {/* Play/Pause button and time */}
        <div className="flex items-center gap-3">
          <Button
            onClick={togglePlayPause}
            disabled={isLoading}
            size="sm"
            className="flex-shrink-0"
            aria-label={isPlaying ? t('audio.pause', { defaultValue: 'Pause' }) : t('audio.play', { defaultValue: 'Lire' })}
          >
            {isLoading ? (
              <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin" />
            ) : isPlaying ? (
              <Pause className="w-4 h-4" />
            ) : (
              <Play className="w-4 h-4" />
            )}
          </Button>

          {/* Progress bar */}
          <div className="flex-1">
            <input
              type="range"
              min="0"
              max={duration || 0}
              value={currentTime}
              onChange={handleSeek}
              className="w-full h-2 bg-neutral-200 rounded-lg appearance-none cursor-pointer accent-primary-600"
              style={{
                background: `linear-gradient(to right, rgb(37, 99, 235) 0%, rgb(37, 99, 235) ${progress}%, rgb(229, 231, 235) ${progress}%, rgb(229, 231, 235) 100%)`,
              }}
              aria-label={t('audio.seek', { defaultValue: 'Position de lecture' })}
            />
          </div>

          {/* Time display */}
          <div className="flex items-center gap-2 text-sm text-neutral-600 min-w-[100px] justify-end">
            <span>{formatTime(currentTime)}</span>
            <span>/</span>
            <span>{formatTime(duration)}</span>
          </div>
        </div>

        {/* Volume control */}
        <div className="flex items-center gap-2">
          <Button
            onClick={toggleMute}
            variant="ghost"
            size="sm"
            className="flex-shrink-0"
            aria-label={isMuted ? t('audio.unmute', { defaultValue: 'Activer le son' }) : t('audio.mute', { defaultValue: 'Couper le son' })}
          >
            {isMuted ? (
              <VolumeX className="w-4 h-4" />
            ) : (
              <Volume2 className="w-4 h-4" />
            )}
          </Button>
          <input
            type="range"
            min="0"
            max="1"
            step="0.1"
            value={isMuted ? 0 : volume}
            onChange={handleVolumeChange}
            className="flex-1 h-1.5 bg-neutral-200 rounded-lg appearance-none cursor-pointer accent-primary-600"
            aria-label={t('audio.volume', { defaultValue: 'Volume' })}
          />
        </div>
      </div>
    </div>
  );
}

export default AudioPlayer;

