/**
 * Options de compression d'image par dÃ©faut
 */
const COMPRESSION_OPTIONS = {
  maxSizeMB: 1,              // Taille maximale : 1MB aprÃ¨s compression
  maxWidthOrHeight: 1920,     // Largeur/hauteur max : 1920px
  useWebWorker: true,         // Utiliser un Web Worker pour ne pas bloquer le UI
  fileType: 'image/jpeg'      // Convertir en JPEG pour optimiser la taille
};

/**
 * Comprimer une image avant upload
 * 
 * @param {File} file - Fichier image Ã  comprimer
 * @param {Object} [options] - Options de compression (override default)
 * 
 * @returns {Promise<{compressedFile: File|null, originalSize: number, compressedSize: number, error: Object|null}>}
 * 
 * @example
 * const result = await compressImage(file);
 * if (result.error) {
 *   console.error('Erreur compression:', result.error.message);
 * } else {
 *   console.log('Compression:', result.originalSize, 'â†’', result.compressedSize);
 *   // Utiliser result.compressedFile pour l'upload
 * }
 */
export async function compressImage(file, options = {}) {
  try {
    if (!file) {
      return {
        compressedFile: null,
        originalSize: 0,
        compressedSize: 0,
        error: {
          message: 'Aucun fichier fourni',
          code: 'NO_FILE'
        }
      };
    }

    // Validation : type d'image autorisÃ©
    if (!storageApi.isAllowedImageType(file.type)) {
      return {
        compressedFile: null,
        originalSize: file.size,
        compressedSize: 0,
        error: {
          message: 'Type de fichier non autorisÃ© (JPEG, PNG, WebP uniquement)',
          code: 'INVALID_FILE_TYPE'
        }
      };
    }

    const originalSize = file.size;
    console.log(`ğŸ—œï¸ Compression en cours... (Taille originale: ${(originalSize / 1024).toFixed(2)}KB)`);

    // Compression avec options par dÃ©faut (ou override)
    const compressedFile = await imageCompression(file, {
      ...COMPRESSION_OPTIONS,
      ...options
    });

    const compressedSize = compressedFile.size;
    const compressionRatio = ((1 - compressedSize / originalSize) * 100).toFixed(1);

    console.log(`âœ… Compression rÃ©ussie: ${(originalSize / 1024).toFixed(2)}KB â†’ ${(compressedSize / 1024).toFixed(2)}KB (${compressionRatio}% rÃ©duit)`);

    return {
      compressedFile,
      originalSize,
      compressedSize,
      error: null
    };

  } catch (err) {
    console.error('âŒ Erreur compression image:', err);
    return {
      compressedFile: null,
      originalSize: file?.size || 0,
      compressedSize: 0,
      error: {
        message: err.message || 'Erreur lors de la compression',
        code: 'COMPRESSION_ERROR'
      }
    };
  }
}

/**
 * Valider les donnÃ©es du formulaire de signalement
 * 
 * @param {Object} formData - DonnÃ©es du formulaire
 * @param {string} formData.type - Type de signalement (OBLIGATOIRE)
 * @param {number} formData.latitude - Latitude GPS (OBLIGATOIRE)
 * @param {number} formData.longitude - Longitude GPS (OBLIGATOIRE)
 * @param {string} [formData.description] - Description
 * @param {string} [formData.phone] - TÃ©lÃ©phone
 * @param {string} [formData.citizen_name] - Nom du citoyen
 * @param {string} [formData.commune_id] - ID de la commune
 * 
 * @returns {{isValid: boolean, errors: Object}}
 * 
 * @example
 * const validation = validateReportData(formData);
 * if (!validation.isValid) {
 *   console.error('Erreurs de validation:', validation.errors);
 * }
 */
export function validateReportData(formData) {
  const errors = {};

  // 1. Type obligatoire
  if (!formData.type || formData.type.trim() === '') {
    errors.type = 'Le type de signalement est obligatoire';
  }

  // 2. Latitude obligatoire et valide (-90 Ã  90)
  if (formData.latitude === null || formData.latitude === undefined) {
    errors.latitude = 'La latitude GPS est obligatoire';
  } else if (typeof formData.latitude !== 'number' || formData.latitude < -90 || formData.latitude > 90) {
    errors.latitude = 'Latitude invalide (doit Ãªtre entre -90 et 90)';
  }

  // 3. Longitude obligatoire et valide (-180 Ã  180)
  if (formData.longitude === null || formData.longitude === undefined) {
    errors.longitude = 'La longitude GPS est obligatoire';
  } else if (typeof formData.longitude !== 'number' || formData.longitude < -180 || formData.longitude > 180) {
    errors.longitude = 'Longitude invalide (doit Ãªtre entre -180 et 180)';
  }

  // 4. Description optionnelle mais si fournie, max 1000 caractÃ¨res
  if (formData.description && formData.description.length > 1000) {
    errors.description = 'Description trop longue (max 1000 caractÃ¨res)';
  }

  // 5. TÃ©lÃ©phone optionnel mais si fourni, format basique (+221...)
  if (formData.phone && formData.phone.trim() !== '') {
    // Format simple : commence par + ou un chiffre, longueur 9-15
    const phoneRegex = /^[\+]?[0-9]{9,15}$/;
    if (!phoneRegex.test(formData.phone.replace(/[\s\-]/g, ''))) {
      errors.phone = 'Format de tÃ©lÃ©phone invalide';
    }
  }

  // 6. Nom du citoyen optionnel mais si fourni, max 100 caractÃ¨res
  if (formData.citizen_name && formData.citizen_name.length > 100) {
    errors.citizen_name = 'Nom trop long (max 100 caractÃ¨res)';
  }

  const isValid = Object.keys(errors).length === 0;

  return { isValid, errors };
}

