/**
 * Calculer les statistiques des signalements d'une commune (AGENT)
 * 
 * @param {string} communeId - UUID de la commune
 * 
 * @returns {Promise<{stats: Object|null, error: Object|null}>}
 * 
 * @example
 * const result = await getAgentStatistics('uuid-dakar');
 * // result.stats = {
 * //   total: 68,
 * //   pending: 12,
 * //   in_progress: 8,
 * //   resolved: 45,
 * //   rejected: 3,
 * //   by_type: { voirie: 20, eclairage: 15, ... }
 * // }
 */
export async function getAgentStatistics(communeId) {
  try {
    if (!communeId) {
      return {
        stats: null,
        error: {
          message: 'ID de la commune requis',
          code: 'MISSING_COMMUNE_ID'
        }
      };
    }

    console.log(`ğŸ“Š Calcul des statistiques pour la commune: ${communeId}`);

    // RÃ©cupÃ©rer TOUS les signalements de la commune (via RLS)
    const { data: reports, error } = await reportApi.getAllReports({
      commune_id: communeId,
      limit: 1000 // Limite haute pour avoir tous les signalements
    });

    if (error) {
      return {
        stats: null,
        error
      };
    }

    // Calcul des statistiques par statut
    const stats = {
      total: reports.length,
      pending: reports.filter(r => r.status === 'pending').length,
      in_progress: reports.filter(r => r.status === 'in_progress').length,
      resolved: reports.filter(r => r.status === 'resolved').length,
      rejected: reports.filter(r => r.status === 'rejected').length,
    };

    // Calcul des statistiques par type (optionnel)
    const byType = {};
    reports.forEach(r => {
      byType[r.type] = (byType[r.type] || 0) + 1;
    });
    stats.by_type = byType;

    // Calcul des statistiques par prioritÃ© (optionnel)
    const byPriority = {};
    reports.forEach(r => {
      byPriority[r.priority] = (byPriority[r.priority] || 0) + 1;
    });
    stats.by_priority = byPriority;

    console.log(`âœ… Statistiques calculÃ©es:`, stats);

    return {
      stats,
      error: null
    };

  } catch (err) {
    console.error('âŒ Erreur calcul statistiques:', err);
    return {
      stats: null,
      error: {
        message: err.message || 'Erreur lors du calcul des statistiques',
        code: 'STATS_ERROR'
      }
    };
  }
}

/**
 * Exporter des signalements en format CSV
 * 
 * @param {Array} reports - Liste des signalements Ã  exporter
 * @param {string} [filename='signalements.csv'] - Nom du fichier CSV
 * 
 * @returns {string} - Contenu CSV (Ã  tÃ©lÃ©charger via un blob)
 * 
 * @example
 * const reports = await getAllReports({ commune_id: 'uuid-dakar' });
 * const csv = exportToCSV(reports.data);
 * // TÃ©lÃ©charger le CSV :
 * const blob = new Blob([csv], { type: 'text/csv;charset=utf-8;' });
 * const link = document.createElement('a');
 * link.href = URL.createObjectURL(blob);
 * link.download = 'signalements-dakar.csv';
 * link.click();
 */
export function exportToCSV(reports) {
  try {
    if (!reports || reports.length === 0) {
      console.warn('âš ï¸ Aucun signalement Ã  exporter');
      return '';
    }

    console.log(`ğŸ“¥ Export CSV de ${reports.length} signalements...`);

    // En-tÃªtes du CSV
    const headers = [
      'ID',
      'Type',
      'Description',
      'Statut',
      'PrioritÃ©',
      'Commune',
      'Latitude',
      'Longitude',
      'Citoyen',
      'TÃ©lÃ©phone',
      'Date crÃ©ation',
      'Date mise Ã  jour'
    ];

    // Convertir les signalements en lignes CSV
    const rows = reports.map(r => [
      r.id || '',
      r.type || '',
      (r.description || '').replace(/"/g, '""'), // Ã‰chapper les guillemets
      r.status || '',
      r.priority || '',
      r.commune?.name || r.commune_id || '',
      r.latitude || '',
      r.longitude || '',
      (r.citizen_name || '').replace(/"/g, '""'),
      r.phone || '',
      r.created_at ? new Date(r.created_at).toLocaleDateString('fr-FR') : '',
      r.updated_at ? new Date(r.updated_at).toLocaleDateString('fr-FR') : ''
    ]);

    // Construire le CSV
    const csvContent = [
      headers.join(','), // Ligne d'en-tÃªtes
      ...rows.map(row => row.map(cell => `"${cell}"`).join(',')) // Lignes de donnÃ©es
    ].join('\n');

    console.log('âœ… CSV gÃ©nÃ©rÃ© avec succÃ¨s');

    return csvContent;

  } catch (err) {
    console.error('âŒ Erreur export CSV:', err);
    return '';
  }
}

/**
 * TÃ©lÃ©charger un CSV automatiquement (helper function)
 * 
 * @param {Array} reports - Liste des signalements
 * @param {string} [filename='signalements.csv'] - Nom du fichier
 */
export function downloadCSV(reports, filename = 'signalements.csv') {
  const csv = exportToCSV(reports);
  
  if (!csv) {
    console.error('âŒ Impossible de gÃ©nÃ©rer le CSV');
    return;
  }

  // CrÃ©er un Blob et dÃ©clencher le tÃ©lÃ©chargement
  const blob = new Blob([csv], { type: 'text/csv;charset=utf-8;' });
  const link = document.createElement('a');
  link.href = URL.createObjectURL(blob);
  link.download = filename;
  link.click();

  console.log(`âœ… TÃ©lÃ©chargement lancÃ©: ${filename}`);
}

